definition.script:
import groovy.json.JsonOutput

def slackNotificationChannel = "#devops-test"
def slackUsername = "Skip the Deployment Skipper"
def twelvehour = (env.Window.replaceAll(".*\\(", "").replaceAll("\\)","").trim())
def twentyfourhour = (env.Window.replaceAll("\\(.*\\)", "").trim())

def notifySlack(text, channel, username, attachments) {
  def slackURL = 'https://hooks.slack.com/services/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'
  def jenkinsIcon = 'https://trekbikescdndev.blob.core.windows.net/hybris/avatars/devops/avatar-02.png'

  def payload = JsonOutput.toJson([text: text,
    channel: channel,
    username: username,
    icon_url: jenkinsIcon,
    attachments: attachments
  ])

  sh "curl -X POST --data-urlencode \'payload=${payload}\' ${slackURL}"
}

def dockerRun(command) {
  def config = "--config home/root/jenkins.rb"
  sh "docker run --add-host=AZL-ChefServer-01:10.16.192.4 \
    -v /home/jenkinsAdmin:/home/root chef/chefdk ${command} \
    ${config}"
}

node {
  try {
    stage('Update Attribute') {
      dockerRun("knife exec -E \'nodes.find(\"name:azl-${env.Environment}-hyb*\") \
      {|n| n.normal[\"dvo_user\"][\"hybris\"][\"auto\"][\"$twentyfourhour\"]=false; n.save}\'")
    }

    stage('Update Crontab') {
      withCredentials([[$class: 'UsernamePasswordMultiBinding', credentialsId: 'local_admin',
      usernameVariable: 'USERNAME', passwordVariable: 'PASSWORD']]) {
        dockerRun("knife ssh \"name:azl-${env.Environment}-hyb*\" \
        \"sudo chef-client -o cb_dvo_hybris::auto\" -x ${env.USERNAME} -P \'${env.PASSWORD}\'")
      }
    }

    stage('Send Slack Message') {
      notifySlack("", slackNotificationChannel, slackUsername, [
        [
          color: "good",
          text: "The following auto-deployment window will be skipped:",
          "mrkdwn_in": ["fields"],
          fields: [
            [
              title: "Timing Window",
              value: "${twelvehour}",
              short: true
            ],
            [
              title: "Node",
              value: "azl-${env.Environment}-hyb-01",
              short: true
            ]
          ]
        ]
      ])
    }
  } catch (e) {
    notifySlack("", slackNotificationChannel, slackUsername, [
      [
        color: "danger",
        text: "Something went wrong while attempting to skip a deployment window:",
        "mrkdwn_in": ["fields"],
        fields: [
          [
            title: "Error",
            value: e.getMessage().toString(),
            short: false
          ]
        ]
      ]
    ])

    throw e
  }
}
