definition.script:
def dockerRun(command) {
  def config = '--config /home/root/jenkins.rb'
  sh "docker run --add-host=AZL-ChefServer-01:10.16.192.4 \
    -v /home/jenkinsAdmin:/home/root chef/chefdk ${command} \
    ${config}"
}

node {  
  stage('Update Version Attribute') {
    dockerRun("knife exec -E 'nodes.find(\"name:azw-${env.Environment}-sql-01\") {|n| n.normal[\"dvo_user\"][\"hybDatabase\"][\"db_version\"]=\"${env.Version}\"; n.save}'")
  }

  stage('Kick-off DB Backup') {
    withCredentials([usernamePassword(credentialsId: 'local_admin', usernameVariable: 'USERNAME', passwordVariable: 'PASSWORD')]) {
      dockerRun("knife winrm \"name:azw-${env.Environment}-sql-01\" \"chef-client -o cb_dvo_hybdatabase::backup\" --winrm-user ${env.USERNAME} --winrm-password \'${env.PASSWORD}\'")
    }
  }
}
