definition.script:
import groovy.json.JsonOutput

env.MYSQL_HOST = 'trek-wordpress-mysql.mysql.database.azure.com'
env.MYSQL_AZURE_SUBSCRIPTION_ID = 'f7274d08-200b-4f00-9989-c077287b3aa3'

def slackNotificationChannel = "#devops-test"
def slackUsername = "The Backerupper"

def notifySlack(text, channel, username, attachments) {
  def slackURL = 'https://hooks.slack.com/services/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'

  def payload = JsonOutput.toJson([text: text,
    channel: channel,
    username: username,
    icon_emoji: ':ronswanson:',
    attachments: attachments
  ])

  sh "curl -X POST --data-urlencode 'payload=${payload}' ${slackURL}"
}

node{
  try {
    stage('Backing up WordPress MySQL database') {
      withCredentials([usernamePassword(credentialsId: 'wordpress_mysql', usernameVariable: 'MYSQL_USER', passwordVariable: 'MYSQL_PASSWORD'),
                       azureServicePrincipal('azureCreds')]) {
        docker.withRegistry('https://conbase.azurecr.io', 'conBase') {
          image = docker.image('conbase.azurecr.io/azbackup:1.0')
          image.pull()
          sh "docker run -e MYSQL_USER -e MYSQL_PASSWORD -e MYSQL_HOST -e AZURE_CLIENT_ID -e AZURE_CLIENT_SECRET -e AZURE_TENANT_ID -e AZURE_SUBSCRIPTION_ID conbase.azurecr.io/azbackup:1.0"
        }
        sh 'docker container prune --force'
      }

      notifySlack("", slackNotificationChannel, slackUsername, [
        [
          color: "good",
          text: "Backed up WordPress MySQL databases.",
          "mrkdwn_in": ["fields"],
          fields: [
            [
                title: "Status",
                value: "Backup Success",
                short: true
            ]
          ]
        ]
      ])
    }
  } catch (e) {
    notifySlack("", slackNotificationChannel, slackUsername, [
      [
        color: "danger",
        text: "Wordpress MySQL backup failed.",
        "mrkdwn_in": ["fields"],
        fields: [
          [
            title: "Error",
            value: "${e}",
            short: false
          ]
        ]
      ]
    ])
    throw e
  }
}
